import React, { Fragment, useRef } from 'react';

// Components
import HeaderColor from '../header-color';

// Utils
import { COLORS } from '../../utils/colors';

// Styles
import './PageLayout.css';

function PageLayout() {
    const mostPickedColor = useRef();

    return (
        <div className={'page-layout'}>
            <section className={'page-layout-colors'}>
                <h3>Colors</h3>
                    { Object.keys(COLORS).map((color, index) =>
                        <Fragment key={index}>
                            <HeaderColor forwardedRef={mostPickedColor} color={color} />
                        </Fragment>
                    )}
                {mostPickedColor &&
                    <p className={'page-layout-counter'}>{`Most clicked color ${mostPickedColor}`}</p>
                }
            </section>
        </div>
    );
}

export default PageLayout;
