import React from 'react';

import { useHeaderContext } from '../../hooks/HeaderContext';

import Circle from '../../media/Circle';
import { COLORS } from '../../utils/colors';

import './PageLayout.css';

function PageLayout() {
    const { setHeaderColor } = useHeaderContext();

    return (
        <div className={'page-layout'}>
            <section className={'page-layout-colors'}>
                <p>Colors</p>
                    { Object.keys(COLORS).map((color, index) => {
                        const matchingColor = Object.keys(COLORS).find(key => key === color);
                        return (
                            <button
                                key={index}
                                className={'page-layout-button'}
                                onClick={() => setHeaderColor(COLORS[matchingColor])}>
                                    <Circle fill={COLORS[matchingColor]} />
                            </button>
                        )
                    })}
            </section>
        </div>
    );
}

export default PageLayout;
