import React, { Fragment, useRef, useState, useCallback, useEffect } from 'react';

// Components
import HeaderColor from '../header-color';

// Utils
import { COLORS } from '../../utils/colors';

// Styles
import './PageLayout.css';

function PageLayout() {
    const [actualColor, setActualColor] = useState(null);
    const prevColorRef = useRef();

    useEffect(() => {
        prevColorRef.current = actualColor;
    }, [actualColor]);

    const prevColor = prevColorRef.current;

    const handleClick = useCallback(color => {
        setActualColor(color);
    }, []);

    return (
        <div className={'page-layout'}>
            <section className={'page-layout-colors'}>
                <h3>Colors</h3>
                    { Object.keys(COLORS).map((color, index) =>
                        <Fragment key={index}>
                            <HeaderColor onClick={handleClick} color={color} />
                        </Fragment>
                    )}
            </section>
        </div>
    );
}

export default PageLayout;
